.toggle.state1:hover { background-color: #4f4 !important; }
.toggle.state2 { background-color: #c88; }
.toggle.state2:hover { background-color: #f44 !important; }
.toggle.state2.hs_button_pressed { background-color: #fcc !important; }
</file>
